mapper.delete:
DELETE FROM QA_INFO
WHERE QA_NO IN
 #{item}

mapper.insert:
INSERT INTO QA_INFO
(QA_NO, Q_NO, ANSWER_YN, SECRET_YN, TITLE, CONTENTS, FILE_NAME, FILE_ORG_NAME, FILE_PATH, REG_USER_NO, REG_DT, PARENT_USER_NO)
VALUES
(
(SELECT IFNULL(MAX(QA.QA_NO),10000000)+1 FROM QA_INFO QA), #{q_no}, 'Y', #{secret_yn}, #{title}, #{contents}, #{file_name}, #{file_org_name}, #{file_path}, #{reg_user_no}, NOW(), #{parent_user_no}
)
